mapper.select:
SELECT mno, id, password, nickname, role, status, points
FROM member
WHERE id = #{id}
AND status != #{status,
typeHandler=world.ssafy.tourtalk.model.typehandler.GenericEnumTypeHandler,
javaType=world.ssafy.tourtalk.model.dto.enums.MemberStatus}

mapper.update:
UPDATE member
SET password = #{password},
nickname =
#{nickname}
WHERE mno = #{mno}

UPDATE curator
SET curator_no = #{curatorNo},
curator_img
= #{curatorImg}
WHERE mno = #{mno}

UPDATE member_details
SET profile_img_path =
#{profileImgPath}
WHERE mno = #{mno}

mapper.select:
SELECT m.mno, m.id, m.nickname, m.role, m.status, d.email, d.phone, d.gender, d.created_at, d.last_login, c.curator_no AS curatorNo FROM member m JOIN member_details d ON m.mno = d.mno LEFT JOIN curator c ON m.mno = c.mno WHERE 1 = 1
AND m.id LIKE CONCAT('%', #{request.keyword}, '%')
AND m.nickname LIKE CONCAT('%', #{request.keyword}, '%')
AND (m.id LIKE CONCAT('%', #{request.keyword}, '%') OR m.nickname LIKE CONCAT('%', #{request.keyword}, '%'))
AND m.role = #{request.role, typeHandler=world.ssafy.tourtalk.model.typehandler.GenericEnumTypeHandler, javaType=world.ssafy.tourtalk.model.dto.enums.MemberRole}
AND m.status = #{request.status, typeHandler=world.ssafy.tourtalk.model.typehandler.GenericEnumTypeHandler, javaType=world.ssafy.tourtalk.model.dto.enums.MemberStatus}
ORDER BY ${request.orderBy} ${request.orderDirection}
ORDER BY d.created_at DESC
LIMIT #{request.pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM member m JOIN member_details d ON m.mno = d.mno WHERE 1 = 1
AND m.id LIKE CONCAT('%', #{request.keyword}, '%')
AND m.nickname LIKE CONCAT('%', #{request.keyword}, '%')
AND (m.id LIKE CONCAT('%', #{request.keyword}, '%') OR m.nickname LIKE CONCAT('%', #{request.keyword}, '%'))
AND m.role = #{request.role, typeHandler=world.ssafy.tourtalk.model.typehandler.GenericEnumTypeHandler, javaType=world.ssafy.tourtalk.model.dto.enums.MemberRole}
AND m.status = #{request.status, typeHandler=world.ssafy.tourtalk.model.typehandler.GenericEnumTypeHandler, javaType=world.ssafy.tourtalk.model.dto.enums.MemberStatus}
