mapper.select:
SELECT COUNT(*)
FROM tour_product
WHERE status = #{status}

	AND title LIKE CONCAT('%', #{keyword}, '%')


	AND EXISTS (
	SELECT 1 FROM tour_time_slot
	WHERE product_id =
	tour_product.id
	AND date = #{date}
	)


	AND max_participants >= #{participantCount}

SELECT tp.product_id AS productId, tp.mno AS mno, tp.location_no AS locationNo, tp.title AS title, tp.description AS description, tp.max_participants AS maxParticipants, tp.min_participants AS minParticipants, tp.price_type AS priceType, tp.price AS price, tp.start_date AS startDate, tp.status AS status, tp.thumbnail_img AS thumbnailImg, tp.tags AS tags, tp.meeting_place AS meetingPlace, tp.meeting_time AS meetingTime, tp.duration AS duration, tp.created_at AS createdAt, tp.updated_at AS updatedAt FROM tour_product tp WHERE tp.status = #{status}
AND tp.title LIKE CONCAT('%', #{keyword}, '%')
AND EXISTS ( SELECT 1 FROM tour_time_slot WHERE product_id = tp.product_id AND date = #{date} )
AND tp.max_participants >= #{participantCount}
ORDER BY ${orderBy} ${orderDirection}
ORDER BY tp.created_at DESC
LIMIT #{pageSize} OFFSET #{offset}
