mapper.select:
SELECT content_type_id 
FROM contenttypes 
WHERE content_type_name = #{content}

SELECT g.gugun_name
FROM sidos s 
JOIN guguns g ON (s.sido_code = g.sido_code)
WHERE s.sido_code = #{sido} 
AND g.gugun_code = #{gugun}

SELECT 
FROM attractions a
JOIN contenttypes c ON a.content_type_id = c.content_type_id



	ORDER BY ${orderBy}


	ORDER BY a.no

LIMIT #{offset}, #{limit}

SELECT gugun_code AS id, gugun_name AS name
FROM guguns


		sido_code = #{code}

SELECT 
FROM attractions a
JOIN contenttypes c ON a.content_type_id = c.content_type_id


	a.first_image1 IS NOT NULL AND a.first_image1 != ''

		AND c.content_type_id = #{contentTypeId}


		AND a.area_code = #{sidoCode}


ORDER BY RAND()
LIMIT #{count}

mapper.update:
UPDATE attractions 
SET view_cnt = view_cnt + 1 
WHERE no = #{no}

mapper.select:
SELECT a.no, a.title, s.sido_name, a.view_cnt FROM attractions a JOIN sidos s ON (a.area_code = s.sido_code)
a.view_cnt >= 1
ORDER BY a.view_cnt DESC LIMIT #{limit}

SELECT 
FROM attractions a
JOIN contenttypes c ON a.content_type_id = c.content_type_id



		(a.title LIKE CONCAT('%', #{keyword}, '%')
		OR a.addr1 LIKE CONCAT('%', #{keyword}, '%')
		OR a.overview LIKE CONCAT('%', #{keyword}, '%'))


		AND c.content_type_id = #{contentTypeId}


		AND a.area_code = #{sidoCode}


		AND a.si_gun_gu_code = #{gugunCode}


ORDER BY 


		a.view_cnt DESC


		a.no


LIMIT #{offset}, #{limit}

