mapper.insert:
INSERT INTO hotplaces (
	user_id, title, latitude, longitude, rating, content_type_id,
	description, review, recommendation_reason
) VALUES (
	#{userId}, #{title}, #{latitude}, #{longitude}, #{rating}, #{contentTypeId},
	#{description}, #{review}, #{recommendationReason}
)

mapper.select:
SELECT 

ORDER BY h.created_at DESC
LIMIT #{offset}, #{limit}

SELECT 

WHERE h.user_id = #{userId}
ORDER BY h.created_at DESC
LIMIT #{offset}, #{limit}

SELECT
(h.title LIKE CONCAT('%', #{keyword}, '%') OR h.description LIKE CONCAT('%', #{keyword}, '%') OR h.review LIKE CONCAT('%', #{keyword}, '%'))
AND h.content_type_id = #{contentTypeId}
ORDER BY h.created_at DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM hotplaces h
(h.title LIKE CONCAT('%', #{keyword}, '%') OR h.description LIKE CONCAT('%', #{keyword}, '%') OR h.review LIKE CONCAT('%', #{keyword}, '%'))
AND h.content_type_id = #{contentTypeId}

SELECT 

ORDER BY h.view_count DESC, h.rating DESC
LIMIT #{limit}
